import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './PortfolioSummary.css';

const formatCurrency = (value) => {
  if (typeof value !== 'number' || isNaN(value)) {
    return '$0.00';
  }
  return new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
    minimumFractionDigits: 2,
    maximumFractionDigits: 2
  }).format(value);
};

const formatPercentage = (value) => {
  if (typeof value !== 'number' || isNaN(value)) {
    return '+0.00%';
  }
  return new Intl.NumberFormat('en-US', {
    style: 'percent',
    minimumFractionDigits: 2,
    maximumFractionDigits: 2,
    signDisplay: 'always'
  }).format(value / 100);
};

const FearGreedIndex = () => {
  const [fearGreedData, setFearGreedData] = useState({ score: 50 });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchFearGreedIndex = async () => {
      try {
        const response = await axios.get('/api/market/fear-greed');
        setFearGreedData(response.data);
        setError(null);
      } catch (err) {
        console.error('Failed to fetch Fear & Greed index:', err);
        setError('Failed to load market sentiment');
      } finally {
        setLoading(false);
      }
    };

    fetchFearGreedIndex();
    // Refresh every 5 minutes
    const interval = setInterval(fetchFearGreedIndex, 5 * 60 * 1000);
    return () => clearInterval(interval);
  }, []);

  let sentiment;
  const value = fearGreedData.score;
  if (value <= 25) sentiment = 'Extreme Fear';
  else if (value <= 45) sentiment = 'Fear';
  else if (value <= 55) sentiment = 'Neutral';
  else if (value <= 75) sentiment = 'Greed';
  else sentiment = 'Extreme Greed';

  if (loading) {
    return (
      <div className="fear-greed-card">
        <div className="fear-greed-header">
          <h2>Fear & Greed Index</h2>
          <div className="sentiment-tag">Loading...</div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="fear-greed-card">
        <div className="fear-greed-header">
          <h2>Fear & Greed Index</h2>
          <div className="sentiment-tag error">{error}</div>
        </div>
      </div>
    );
  }

  return (
    <div className="fear-greed-card">
      <div className="fear-greed-header">
        <h2>Fear & Greed Index</h2>
        <div className="sentiment-tag">{sentiment}</div>
      </div>
      <div className="fear-greed-scale">
        <div className="scale-bar">
          <div className="scale-indicator" style={{ left: `${value}%` }}></div>
        </div>
        <div className="scale-labels">
          <span>Extreme Fear</span>
          <span>Neutral</span>
          <span>Extreme Greed</span>
        </div>
      </div>
      <div className="fear-greed-details">
        <p>Current Score: {value}</p>
        <p>Previous Close: {fearGreedData.previousClose}</p>
        <p>1 Week Ago: {fearGreedData.oneWeekAgo}</p>
        <p>1 Month Ago: {fearGreedData.oneMonthAgo}</p>
      </div>
      <p className="fear-greed-description">
        The Fear & Greed Index measures market sentiment from 0 (extreme fear) to 100 (extreme greed), helping identify potential buying or selling opportunities.
      </p>
    </div>
  );
};

const PortfolioSummary = ({ portfolio }) => {
  const { positions = [] } = portfolio;

  // Calculate total portfolio value with safety checks
  const totalValue = positions.reduce((sum, position) => {
    const currentPrice = position.currentPrice || position.averagePrice || 0;
    return sum + (position.shares * currentPrice);
  }, 0);

  // Calculate total gain/loss with safety checks
  const totalCost = positions.reduce((sum, position) => {
    return sum + (position.shares * (position.averagePrice || 0));
  }, 0);
  const totalGainLoss = totalValue - totalCost;
  const totalGainLossPercentage = totalCost > 0 ? (totalGainLoss / totalCost) * 100 : 0;

  // Calculate today's change with safety checks
  const todaysChange = positions.reduce((sum, position) => {
    if (!position.currentPrice || !position.dailyChange) return sum;
    const previousClose = position.currentPrice / (1 + (position.dailyChange / 100));
    return sum + (position.shares * (position.currentPrice - previousClose));
  }, 0);

  return (
    <div className="portfolio-dashboard">
      <div className="dashboard-header">
        <div>
          <h1>Portfolio Dashboard</h1>
          <p className="subtitle">Track your investments with real market data</p>
        </div>
        <div className="header-actions">
          <button className="refresh-btn">â†» Refresh</button>
          <button className="add-stock-btn">+ Add Stock</button>
        </div>
      </div>

      <div className="dashboard-grid">
        <div className="dashboard-card">
          <div className="card-header">
            <span className="card-title">Portfolio Value</span>
            <span className="card-icon">$</span>
          </div>
          <div className="card-value">{formatCurrency(totalValue)}</div>
          <div className="card-subtext">Total market value</div>
        </div>

        <div className="dashboard-card">
          <div className="card-header">
            <span className="card-title">Total Gain/Loss</span>
            <span className="card-icon">ðŸ“ˆ</span>
          </div>
          <div className="card-value" style={{ color: totalGainLoss >= 0 ? '#0ECB81' : '#F6465D' }}>
            {formatCurrency(totalGainLoss)}
          </div>
          <div className="card-subtext">{formatPercentage(totalGainLossPercentage)}</div>
        </div>

        <div className="dashboard-card">
          <div className="card-header">
            <span className="card-title">Today's Change</span>
            <span className="card-icon">ðŸ“Š</span>
          </div>
          <div className="card-value" style={{ color: todaysChange >= 0 ? '#0ECB81' : '#F6465D' }}>
            {formatCurrency(todaysChange)}
          </div>
          <div className="card-subtext">Since market open</div>
        </div>

        <div className="dashboard-card">
          <div className="card-header">
            <span className="card-title">Holdings</span>
            <span className="card-icon">ðŸŽ¯</span>
          </div>
          <div className="card-value">{positions.length}</div>
          <div className="card-subtext">Total positions</div>
        </div>
      </div>

      <div className="section-header">
        <h2>Historical Performance</h2>
      </div>

      <div className="historical-grid">
        <div className="historical-card">
          <div className="historical-header">
            <span>1D</span>
            <button className="export-btn">ðŸ“Š</button>
          </div>
          <div className="historical-value">N/A</div>
          <div className="historical-subtext">No data</div>
        </div>
        <div className="historical-card">
          <div className="historical-header">
            <span>1W</span>
            <button className="export-btn">ðŸ“Š</button>
          </div>
          <div className="historical-value">N/A</div>
          <div className="historical-subtext">No data</div>
        </div>
        <div className="historical-card">
          <div className="historical-header">
            <span>1M</span>
            <button className="export-btn">ðŸ“Š</button>
          </div>
          <div className="historical-value">N/A</div>
          <div className="historical-subtext">No data</div>
        </div>
        <div className="historical-card">
          <div className="historical-header">
            <span>1Y</span>
            <button className="export-btn">ðŸ“Š</button>
          </div>
          <div className="historical-value">N/A</div>
          <div className="historical-subtext">No data</div>
        </div>
      </div>

      <div className="historical-note">
        Historical gains calculated from current prices vs historical price data. 1D = past day, 1W = past week, 1M = past month, 1Y = past year.
      </div>

      <div className="dashboard-grid-extended">
        <div className="holdings-section">
          <div className="section-header">
            <h2>Your Holdings</h2>
            <span className="section-subtitle">Sorted by portfolio value</span>
          </div>

          <div className="holdings-list">
            {positions
              .sort((a, b) => {
                const aValue = (b.currentPrice || b.averagePrice || 0) * b.shares;
                const bValue = (a.currentPrice || a.averagePrice || 0) * a.shares;
                return aValue - bValue;
              })
              .map(position => {
                const currentPrice = position.currentPrice || position.averagePrice || 0;
                const positionValue = position.shares * currentPrice;
                const positionCost = position.shares * (position.averagePrice || 0);
                const positionGain = positionValue - positionCost;
                const positionGainPercentage = positionCost > 0 ? (positionGain / positionCost) * 100 : 0;

                return (
                  <div key={position.symbol} className="holding-item">
                    <div className="holding-info">
                      <div className="holding-symbol">
                        <span className="symbol">{position.symbol}</span>
                        <span className="sector-tag">{position.sector || 'N/A'}</span>
                      </div>
                      <div className="holding-name">{position.name || position.symbol}</div>
                      <div className="holding-details">
                        {position.shares} shares Ã— purchased at {formatCurrency(position.averagePrice)}
                      </div>
                    </div>
                    <div className="holding-value">
                      <div className="current-value">
                        <div className="value-label">VALUE</div>
                        <div className="value-amount">{formatCurrency(positionValue)}</div>
                        <div className="value-change" style={{ color: positionGain >= 0 ? '#0ECB81' : '#F6465D' }}>
                          {formatCurrency(positionGain)} ({formatPercentage(positionGainPercentage)})
                        </div>
                      </div>
                    </div>
                  </div>
                );
              })}
          </div>
        </div>

        <div className="market-sentiment">
          <FearGreedIndex />
        </div>
      </div>
    </div>
  );
};

export default PortfolioSummary;
