import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from './components/Header';
import StockCard from './components/StockCard';
import CurrencyCard from './components/CurrencyCard';
import BitcoinCard from './components/BitcoinCard';
import LoadingSpinner from './components/LoadingSpinner';
import PortfolioPage from './components/Portfolio/PortfolioPage';
import './App.css';

function App() {
  const [activeTab, setActiveTab] = useState('portfolio'); // market, portfolio
  const [stocks, setStocks] = useState([]);
  const [usdIls, setUsdIls] = useState(null);
  const [bitcoin, setBitcoin] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastUpdated, setLastUpdated] = useState(null);

  const fetchUsdIlsData = async () => {
    try {
      const response = await axios.get('/api/forex/usdils');
      setUsdIls(response.data);
    } catch (err) {
      console.error('Error fetching USD/ILS data:', err);
      setUsdIls({
        symbol: 'USD/ILS',
        rate: 0,
        previousRate: 0,
        dailyChange: 0,
        dailyChangePercent: 0,
        timestamp: new Date().toISOString(),
        lastUpdated: 'N/A',
        error: 'Failed to fetch rate'
      });
    }
  };

  const fetchStockData = async () => {
    try {
      setLoading(true);
      // Define the tickers to display in market overview
      const tickers = [
        // Major ETFs and Indices
        'SPY', 'QQQ', 'IWM', 'RSP', 'MAGS',
        // Volatility ETFs (VIX alternatives)
        'VXX', 'UVXY', 'SVXY',
        // Individual stocks from your portfolio
        'AAPL', 'MSFT', 'GOOGL', 'AMZN', 'TSLA', 'NVDA', 'META',
        // Specific requested stocks
        'PLTR', 'HIMS',
        // Crypto stocks (Bitcoin and Ethereum focused)
        'IBIT', 'ETHE', 'ETHW'
      ];

      // Fetch both stock data and USD/ILS data in parallel
      const [stockResponse] = await Promise.all([
        axios.get(`/api/stocks/snapshots?symbols=${tickers.join(',')}`),
        fetchUsdIlsData()
      ]);

      if (stockResponse.data && stockResponse.data.snapshots) {
        const stockData = Object.entries(stockResponse.data.snapshots)
          .filter(([symbol, data]) => {
            // Filter out symbols with no data
            return data && (data.latestTrade?.p || data.latestQuote?.ap || data.latestQuote?.bp);
          })
          .map(([symbol, data]) => {
            // Use latestTrade price if available, otherwise use latestQuote ask/bid
            const currentPrice = data.latestTrade?.p || data.latestQuote?.ap || data.latestQuote?.bp || 0;
            const openPrice = data.dailyBar?.o || 0;
            const prevClose = data.prevDailyBar?.c || 0;

            // Calculate change from previous close if available, otherwise from open
            const referencePrice = prevClose || openPrice;
            const change = referencePrice ? (currentPrice - referencePrice) : 0;
            const changePercent = referencePrice ? ((change / referencePrice) * 100) : 0;

            return {
              symbol,
              price: currentPrice,
              change,
              changePercent,
              volume: data.dailyBar?.v || 0,
              high: data.dailyBar?.h || 0,
              low: data.dailyBar?.l || 0,
              open: openPrice,
              close: prevClose
            };
          });

        setStocks(stockData);
        setLastUpdated(new Date());

        // Show info message if some symbols were filtered out
        if (stockData.length < tickers.length) {
          console.log(`Note: ${tickers.length - stockData.length} symbols were not available or had no data`);
          console.log('Note: VIX, TA35, and TA125 are not supported by Alpaca API. Using VXX, UVXY, SVXY as VIX alternatives.');
        }

        setError(null);
      } else {
        setError('No market data available. Please check your API configuration.');
      }
    } catch (err) {
      console.error('Error fetching stock data:', err);
      setError('Failed to fetch stock data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (activeTab === 'market') {
      fetchStockData();

      // Auto-refresh every 30 seconds only when market tab is active
      const interval = setInterval(fetchStockData, 30000);

      return () => clearInterval(interval);
    }
  }, [activeTab]);

  const renderContent = () => {
    if (loading && stocks.length === 0) {
      return <LoadingSpinner />;
    }

    if (activeTab === 'market') {
      return (
        <>
          {error && (
            <div className="error-message">
              {error}
              <button onClick={fetchStockData} className="retry-button">
                Retry
              </button>
            </div>
          )}

          <div className="market-overview">
            <div className="market-section">
              <h2 className="section-title">Currency</h2>
              <div className="stocks-grid">
                {usdIls && <CurrencyCard currency={usdIls} />}
              </div>
            </div>

            <div className="market-section">
              <h2 className="section-title">Major Indices & ETFs</h2>
              <div className="stocks-grid">
                {stocks
                  .filter(stock => ['SPY', 'QQQ', 'IWM', 'RSP', 'MAGS'].includes(stock.symbol))
                  .map((stock) => (
                    <StockCard key={stock.symbol} stock={stock} />
                  ))}
              </div>
            </div>

            <div className="market-section">
              <h2 className="section-title">Volatility ETFs</h2>
              <div className="stocks-grid">
                {stocks
                  .filter(stock => ['VXX', 'UVXY', 'SVXY'].includes(stock.symbol))
                  .map((stock) => (
                    <StockCard key={stock.symbol} stock={stock} />
                  ))}
              </div>
            </div>

            <div className="market-section">
              <h2 className="section-title">Individual Stocks</h2>
              <div className="stocks-grid">
                {stocks
                  .filter(stock => ['AAPL', 'MSFT', 'GOOGL', 'AMZN', 'TSLA', 'NVDA', 'META', 'PLTR', 'HIMS'].includes(stock.symbol))
                  .map((stock) => (
                    <StockCard key={stock.symbol} stock={stock} />
                  ))}
              </div>
            </div>

                          <div className="market-section">
              <h2 className="section-title">Bitcoin & Ethereum</h2>
              <div className="stocks-grid">
                {stocks
                  .filter(stock => ['IBIT', 'ETHE', 'ETHW'].includes(stock.symbol))
                  .map((stock) => (
                    <StockCard key={stock.symbol} stock={stock} />
                  ))}
              </div>
            </div>
          </div>

          {stocks.length === 0 && !loading && (
            <div className="no-data">
              <p>No stock data available.</p>
              <button onClick={fetchStockData} className="retry-button">
                Load Data
              </button>
            </div>
          )}
        </>
      );
    }

    return <PortfolioPage />;
  };

  return (
    <div className="app">
      <Header 
        lastUpdated={activeTab === 'market' ? lastUpdated : null}
        onRefresh={activeTab === 'market' ? fetchStockData : null}
      />

      <div className="tabs">
        <button
          className={`tab-button ${activeTab === 'market' ? 'active' : ''}`}
          onClick={() => setActiveTab('market')}
        >
          Market Overview
        </button>
        <button
          className={`tab-button ${activeTab === 'portfolio' ? 'active' : ''}`}
          onClick={() => setActiveTab('portfolio')}
        >
          Portfolio Management
        </button>
      </div>

      <div className="content">
        {renderContent()}
      </div>
    </div>
  );
}

export default App;
