import React, { useState, useEffect } from 'react';
import axios from 'axios';
import StockCard from './components/StockCard';
import Header from './components/Header';
import LoadingSpinner from './components/LoadingSpinner';
import './App.css';

function App() {
  const [stocks, setStocks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [lastUpdated, setLastUpdated] = useState(null);

  const fetchStockData = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/api/stocks/snapshots');

      if (response.data && response.data.snapshots) {
        const stockData = Object.entries(response.data.snapshots).map(([symbol, data]) => ({
          symbol,
          price: data.latestQuote?.ap || data.latestQuote?.bp || 0,
          change: data.dailyBar ? (data.latestQuote?.ap - data.dailyBar.o) : 0,
          changePercent: data.dailyBar ? (((data.latestQuote?.ap - data.dailyBar.o) / data.dailyBar.o) * 100) : 0,
          volume: data.dailyBar?.v || 0,
          high: data.dailyBar?.h || 0,
          low: data.dailyBar?.l || 0,
          open: data.dailyBar?.o || 0,
          close: data.prevDailyBar?.c || 0
        }));

        setStocks(stockData);
        setLastUpdated(new Date());
        setError(null);
      }
    } catch (err) {
      console.error('Error fetching stock data:', err);
      setError('Failed to fetch stock data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchStockData();

    // Auto-refresh every 30 seconds
    const interval = setInterval(fetchStockData, 30000);

    return () => clearInterval(interval);
  }, []);

  if (loading && stocks.length === 0) {
    return (
      <div className="app">
        <Header />
        <LoadingSpinner />
      </div>
    );
  }

  return (
    <div className="app">
      <Header lastUpdated={lastUpdated} onRefresh={fetchStockData} />

      {error && (
        <div className="error-message">
          {error}
          <button onClick={fetchStockData} className="retry-button">
            Retry
          </button>
        </div>
      )}

      <div className="stocks-grid">
        {stocks.map((stock) => (
          <StockCard key={stock.symbol} stock={stock} />
        ))}
      </div>

      {stocks.length === 0 && !loading && (
        <div className="no-data">
          <p>No stock data available.</p>
          <button onClick={fetchStockData} className="retry-button">
            Load Data
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
