import React from 'react';
import './PortfolioSummary.css';

const formatCurrency = (value) => {
  return new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
    minimumFractionDigits: 2,
    maximumFractionDigits: 2
  }).format(value);
};

const formatPercentage = (value) => {
  return new Intl.NumberFormat('en-US', {
    style: 'percent',
    minimumFractionDigits: 2,
    maximumFractionDigits: 2,
    signDisplay: 'always'
  }).format(value / 100);
};

const DashboardCard = ({ title, value, subtext, icon, valueColor }) => (
  <div className="dashboard-card">
    <div className="card-header">
      <span className="card-title">{title}</span>
      <span className={`card-icon ${icon}`}>{icon}</span>
    </div>
    <div className="card-value" style={{ color: valueColor }}>{value}</div>
    <div className="card-subtext">{subtext}</div>
  </div>
);

const HistoricalPerformanceCard = ({ period, value, isExportable }) => (
  <div className="historical-card">
    <div className="historical-header">
      <span>{period}</span>
      {isExportable && <button className="export-btn">📊</button>}
    </div>
    <div className="historical-value">
      {value || 'N/A'}
    </div>
    <div className="historical-subtext">
      {value ? '' : 'No data'}
    </div>
  </div>
);

const PortfolioSummary = ({ portfolio }) => {
  const { summary } = portfolio;

  return (
    <div className="portfolio-dashboard">
      <div className="dashboard-header">
        <div>
          <h1>Portfolio Dashboard</h1>
          <p className="subtitle">Track your investments with real market data</p>
        </div>
        <div className="header-actions">
          <button className="refresh-btn">↻ Refresh</button>
          <button className="add-stock-btn">+ Add Stock</button>
        </div>
      </div>

      <div className="dashboard-grid">
        <DashboardCard
          title="Portfolio Value"
          value={formatCurrency(summary.totalValue)}
          subtext="Total market value"
          icon="$"
        />
        <DashboardCard
          title="Total Gain/Loss"
          value={formatCurrency(summary.totalOverallGain)}
          subtext={formatPercentage(summary.totalOverallGainPercentage)}
          icon="📈"
          valueColor={summary.totalOverallGain >= 0 ? '#0ECB81' : '#F6465D'}
        />
        <DashboardCard
          title="Today's Change"
          value={formatCurrency(summary.totalDailyGain)}
          subtext="Since market open"
          icon="📊"
          valueColor={summary.totalDailyGain >= 0 ? '#0ECB81' : '#F6465D'}
        />
        <DashboardCard
          title="Holdings"
          value={portfolio.positions.length}
          subtext="Total positions"
          icon="🎯"
        />
      </div>

      <div className="section-header">
        <h2>Historical Performance</h2>
      </div>

      <div className="historical-grid">
        <HistoricalPerformanceCard period="1D" isExportable={true} />
        <HistoricalPerformanceCard period="1W" isExportable={true} />
        <HistoricalPerformanceCard period="1M" isExportable={true} />
        <HistoricalPerformanceCard period="1Y" isExportable={true} />
      </div>

      <div className="historical-note">
        Historical gains calculated from current prices vs historical price data. 1D = past day, 1W = past week, 1M = past month, 1Y = past year.
      </div>

      <div className="section-header">
        <h2>Your Holdings</h2>
        <span className="section-subtitle">Sorted by portfolio value</span>
      </div>

      <div className="holdings-list">
        {portfolio.positions.map((position) => (
          <div key={position.symbol} className="holding-item">
            <div className="holding-info">
              <div className="holding-symbol">
                <span className="symbol">{position.symbol}</span>
                <span className="sector-tag">{position.sector}</span>
              </div>
              <div className="holding-name">{position.name || 'Stock'}</div>
              <div className="holding-details">
                {position.shares} shares × purchased at {formatCurrency(position.averagePrice)}
              </div>
            </div>
            <div className="holding-value">
              <div className="current-value">
                <div className="value-label">VALUE</div>
                <div className="value-amount">{formatCurrency(position.totalValue)}</div>
                <div className="value-change" style={{ color: position.overallGain >= 0 ? '#0ECB81' : '#F6465D' }}>
                  {formatCurrency(position.overallGain)} ({formatPercentage(position.overallGainPercentage)})
                </div>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PortfolioSummary;
