            <span className="card-title">Total Gain/Loss</span>
            <span className="card-icon">ðŸ“ˆ</span>
          </div>
          <div className="card-value" style={{ color: totalGainLoss >= 0 ? '#0ECB81' : '#F6465D' }}>
            {formatCurrency(totalGainLoss)}
          </div>
          <div className="card-subtext">{formatPercentage(totalGainLossPercentage)}</div>
        </div>

        <div className="dashboard-card">
          <div className="card-header">
            <span className="card-title">Today's Change</span>
            <span className="card-icon">ðŸ“Š</span>
          </div>
